import { Link } from 'react-router-dom';
import { useHistory } from "react-router-dom";

function Menu({title}) {
  //const navigate = useNavigate();
  const history = useHistory();

  function handleClick() {
    //navigate(`/${title}`);
    
  }
  // useEffect(() => {
  //   handleClick();
  // },)

  return (
    <div className='menu'>
        <div className='menu-title' onClick={() => history.push(`/${title}`)}>
          <Link to={`/${title}`}/>
          {title}
        </div>
    </div>
  )
}

export default Menu
